function modalShow() {
    let modal1 = document.querySelector(".modal");
    modal1.classList.add('shown');
}

function modalHide() {
    let modal2 = document.querySelector(".modal");
    modal2.classList.remove('shown');
}

function modalFormShow() {
    let modal1 = document.querySelector(".modal-form");
    modal1.classList.add('shown');
}

function modalFormHide() {
    let modal2 = document.querySelector(".modal-form");
    modal2.classList.remove('shown');
}

